import React, { useEffect } from 'react'
import { useAppDispatch, useAppSelector } from '../hooks'
import { MDBSpinner, MDBCard } from 'mdb-react-ui-kit';
import { getToursByUser } from '../states/reducers/tour-reducer';


const Dashboard = () => {
    const { tour } = useAppSelector(tourData => tourData);
    const { user } = useAppSelector(tourData => tourData.auth);
    const userId = user ? user.id : null;
    const dispatch = useAppDispatch();

    useEffect(() => {
        if (userId) {
            dispatch(getToursByUser(userId));
        }
    }, [userId]);

    if (tour.loading) {
        return <MDBSpinner size="lg" />
    }

    return (
        <div
            style={{
                alignContent: "center",
                margin: "auto",
                padding: 120,
                maxWidth: 900
            }}
        >
            <h2 className='text-center'>Dashboard: {user?.name}</h2>
            <hr style={{ maxWidth: "600px" }} />
            {
                tour.userTours && tour.userTours.map(tour => {
                    return (<h1 key={tour.id}>{tour.name}</h1>)
                })
            }
        </div>
    );
}

export default Dashboard;
