import React from 'react'
import { Tour } from '../../types';

interface Props {
    windowWidth: number;
    tour: Tour;
}

const TourCard = ({ tour, windowWidth }: Props) => {
    const { title, imageFile, description, tags, name } = tour;

    const trimText = (text: string): string => {
        let subStr = text.substring(0, 150);
        if (text.length > 150) {
            subStr += "...";
        }
        return subStr;
    }

    return (
        <div style={
            {
                borderTopLeftRadius: 10,
                borderTopRightRadius: 10,
                minWidth: 300,
                height: 400,
                margin: 20,
                width: windowWidth * .22,
                backgroundColor: 'white',
                boxShadow: "0px 0px 10px 5px rgba(0, 0, 0, 0.3)",
            }
        }>
            <div style={{
                backgroundImage: `url(${imageFile})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat',
                display: 'flex',
                width: "100%",
                height: "50%",
                borderTopRightRadius: 10,
                borderTopLeftRadius: 10,
                justifyContent: "flex-start",
                border: "1px solid black",
                color: "white"
            }}>
                <h3 style={{ marginTop: 4, marginLeft: 5 }}>{name}</h3>
            </div>
            <div style={{
                display: 'flex',
                marginTop: 20,
                marginLeft: 10,
                justifyContent: "flex-start"
            }}>
                {
                    tags.map(tag => <p style={{ marginRight: 2 }}>{`#${tag}`}</p>)
                }
            </div>
            <div style={{
                display: 'flex',
                textAlign: 'right',
                padding: 10,
                lineHeight: 2,
                lineBreak: 'auto',
                justifyContent: "flex-start"
            }}>{trimText(description)}</div>

        </div>
    )
}

export default TourCard;
