import { ActionTypes } from "../action-types";
import { Actions } from "../actions/signin-action";


export interface SigninState {
    email: string;
    password: string;
    loading: boolean;
    error: string;
};


const INITIAL_STATE: SigninState = {
    email: "",
    password: "",
    loading: false,
    error: ""
};


const signinReducer = (state: SigninState = INITIAL_STATE, action: Actions): SigninState => {
    switch (action.type) {
        case ActionTypes.SIGN_IN:
            action.payload
        default:
            return INITIAL_STATE;
    }
}


export default signinReducer;
