import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { toast as ToastType } from 'react-toastify';
import { AxiosError } from "axios";
import { NavigateFunction } from "react-router-dom";
import { signin as signinRoute } from '../../api';


const initialState = {
    user: null,
    error: "",
    loading: false
};


interface AuthParams {
    email: string;
    password: string;
    navigate: NavigateFunction;
    toast: typeof ToastType;
}

const formatError = (error: any): string => {
    return error instanceof AxiosError
        ? error.response?.data.error
        : "Something went wrong on the server";
}

export const login = createAsyncThunk(
    "/auth/signin",
    async (signinParams: AuthParams, { rejectWithValue }) => {
        try {
            const response = await signinRoute(signinParams)
            signinParams.toast.success("Login Succesfully!");
            signinParams.navigate('/');
            return response.data;
        } catch (error) {
            if (error instanceof Error) {
                return rejectWithValue(formatError(error));
            }
        }
    })

const authSlice = createSlice({
    name: "Auth",
    initialState,
    reducers: {
    },
    extraReducers: {
    }
});


