import React, { useState } from 'react'
import {
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBValidation,
    MDBTextArea,
    MDBBtn,
    MDBValidationItem
} from 'mdb-react-ui-kit';
import ChipInput from 'material-ui-chip-input';
import FileBase from 'react-file-base64';


const AddTour = () => {

    const onAddTour = (chip: any) => {
        console.log("add new chip");
    }

    const onDeleteTour = (chip: any, index: number) => {
        console.log("delete chip");
    }

    const onClear = () => {
        console.log("Clear the form");
    }

    return (
        <div style={{
            margin: "auto",
            marginTop: '120px',
            alignContent: 'center',
            maxWidth: '450px'
        }}>
            <MDBCard alignment='center'>
                <MDBCardBody>
                    <h5 style={{ marginBottom: "20px", color: "#0d6efd" }}> Create Tour</h5>
                    <MDBValidation className='row g-3' noValidate >
                        <div
                        >
                            <MDBValidationItem
                                feedback="Please provide a title"
                                invalid
                            >
                                <MDBInput
                                    label="title"
                                    // value=''
                                    onChange={() => console.log("input changed")}
                                    required

                                />
                            </MDBValidationItem>
                        </div>
                        <div>
                            <MDBTextArea
                                placeholder="Type description"
                                className='mt-3'
                            // value=''
                            />
                        </div>
                        <div>
                            <ChipInput
                                className="mt-2"
                                style={{ width: "100%" }}
                                onAdd={(chip: any) => onAddTour(chip)}
                                onDelete={(chip: any, index: number) => onDeleteTour(chip, index)}
                                placeholder="Enter tags"
                            />
                        </div>
                        <FileBase
                            multiple={false}
                            onDone={(files: any) => console.log(files)}
                        />
                        <MDBBtn
                            role="statue"
                            className='mt-4'
                        >
                            Create
                        </MDBBtn>
                        <MDBBtn
                            role="statue"
                            className='mt-3 btn-danger'
                        >
                            Clear
                        </MDBBtn>

                    </MDBValidation>
                </MDBCardBody>
            </MDBCard>
        </div>
    );
}

export default AddTour;
