import { ActionTypes } from "../action-types";
import { Actions } from '../actions/signin-action';
import { signin as signinRoute } from '../../api'
import { Dispatch } from "react";
import localforage from "localforage";
import { NavigateFunction } from "react-router-dom";
import { toast as toastType } from "react-toastify";


interface SigninParams {
    email: string;
    password: string;
    navigate: NavigateFunction;
    toast: typeof toastType;
}

export const signin = (signinParams: SigninParams) => {
    return async (dispatch: Dispatch<Actions>) => {
        dispatch({
            type: ActionTypes.SIGNIN_LOADING,
        });
        try {
            const { data } = await signinRoute(signinParams)
            await localforage.setItem('profile', JSON.stringify(data));
            signinParams.toast.success("Login Succesfully!");
            signinParams.navigate('/');
            dispatch({
                type: ActionTypes.SIGN_IN,
                payload: data
            });
        } catch (err) {
            const error = err instanceof Error ? err.message : "Could not contact server";
            dispatch({
                type: ActionTypes.SIGNIN_ERROR,
                payload: { error }
            });
            signinParams.toast.error("Login failed");
        }
    }
}
