import React from 'react'
import { Tour } from '../../types';

interface Props {
    windowWidth: number;
    tour: Tour;
}

const TourCard = ({ tour, windowWidth }: Props) => {
    const { title, imageFile, description, tags, name } = tour;
    return (
        <div style={
            {
                borderRadius: 10,
                minWidth: 300,
                height: 400,
                margin: 20,
                width: windowWidth * .22,
                backgroundColor: 'white',
                boxShadow: "0px 0px 10px 5px rgba(0, 0, 0, 0.3)",
            }
        }>
            {/* <img style={{
                height: '45%',
                width: "100%",
                borderTopRightRadius: 10,
                borderTopLeftRadius: 10
            }}
                src={imageFile}
                alt="image error" />
            <div style={{ position: "relative", top: 10, left: 10,}}>{name}</div> */}
            <div style={{
                backgroundImage: `url(${imageFile})`,
                display: 'flex',
                width: "100%",
                height: "50%",
                borderTopRightRadius: 10,
                borderTopLeftRadius: 10,
                justifyContent: "flex-start",
                border: "1px solid black",
                color: "white"
            }}>
                <h3 style={{ marginTop: 4, marginLeft: 5}}>{name}</h3>
            </div>
            <div style={{}}>
                tags.map(tag => return <h5>{`#${tag}`}</h5>)
            </div>

        </div>
    )
}

export default TourCard;
