import React from 'react'
import { MDBCard, MDBCardBody, MDBInput, MDBValidation, MDBTextArea } from 'mdb-react-ui-kit';
import ChipInput from 'material-ui-chip-input';


const AddTour = () => {

    const onAddTour = (chip: any) => {
        console.log("add new chip");
    }

    const onDeleteTour = (chip: any, index: number) => {
        console.log("delete chip");
    }

    return (
        <div style={{
            margin: "auto",
            marginTop: '120px',
            alignContent: 'center',
            maxWidth: '450px'
        }}>
            <MDBCard alignment='center'>
                <MDBCardBody>
                    <MDBValidation className='row g-3'>
                        <MDBInput
                            label="title"
                            // value=''
                            onChange={() => console.log("input changed")}
                            required
                        />
                        <MDBTextArea 
                        label="Type description"
                        className='mt-3'
                        // value=''
                        />
                        <ChipInput 
                        className="mt-2"
                        onAdd={(chip: any) => onAddTour(chip)}
                        onDelete={(chip: any, index: number) => onDeleteTour(chip, index)}

                        />
                    </MDBValidation>
                </MDBCardBody>
            </MDBCard>
        </div>
    );
}

export default AddTour;
