import React, { useEffect } from 'react'
import { useAppDispatch, useAppSelector } from '../hooks'
import { MDBSpinner, MDBCard, MDBRow, MDBCol, MDBCardImage, MDBCardTitle, MDBCardText } from 'mdb-react-ui-kit';
import { getToursByUser } from '../states/reducers/tour-reducer';


const Dashboard = () => {
    const { tour } = useAppSelector(tourData => tourData);
    const { user } = useAppSelector(tourData => tourData.auth);
    const userId = user ? user.id : null;
    const dispatch = useAppDispatch();

    useEffect(() => {
        if (userId) {
            dispatch(getToursByUser(userId));
        }
    }, [userId]);

    if (tour.loading) {
        return <MDBSpinner size="lg" />
    }


    const truncateText = (text: string): string => text.length > 10 ? `${text.substring(0, 250)}...` : text;


    return (
        <div
            style={{
                alignContent: "center",
                margin: "auto",
                padding: 120,
                maxWidth: 900
            }}
        >
            <h2 className='text-center'>Dashboard: {user?.name}</h2>
            <hr style={{ maxWidth: "600px" }} />
            {
                tour.userTours && tour.userTours.map(tour => {
                    return (
                        <MDBCard style={{ maxWidth: "450px" }}>
                            <MDBRow>
                                <MDBCol md="4">
                                    <MDBCardImage src={tour.imageFile} alt="..." fluid />
                                </MDBCol>
                                <MDBCol md="8">
                                    <MDBCardTitle>{tour.title}</MDBCardTitle>
                                    <MDBCardText>{truncateText(tour.description)}</MDBCardText>
                                </MDBCol>
                            </MDBRow>
                        </MDBCard>
                    );
                })
            }
        </div>
    );
}

export default Dashboard;
