import { configureStore, applyMiddleware, MiddlewareArray } from '@reduxjs/toolkit';
import reducers from './reducers';
import thunkMiddleware from 'redux-thunk';



const store = configureStore({
    reducer: reducers,
    middleware: [thunk] 
});

export default store;
