import React, { useState, useEffect } from 'react'
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBIcon,
    MDBValidation,
    MDBValidationItem,
    MDBFooter
} from 'mdb-react-ui-kit';
import { Link } from 'react-router-dom';
import { useAction } from '../hooks/use-action';

type Props = {}


interface Form {
    email: string;
    password: string;
}

export const Signin = ({ }: Props) => {


    const [formValue, setFormValue] = useState<Form>({ email: "", password: "" })

    const { signin } = useAction();
    const onSubmit = (event: React.FormEvent) => {
        event.preventDefault();
        signin(formValue);
    }

    const onFormChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        event.preventDefault();
        const { name, value } = event.target;
        setFormValue({ ...formValue, [name]: value });
    }

    return (
        <div style={{ alignContent: "center", margin: "auto", maxWidth: 450, marginTop: 120 }} >
            <MDBCard alignment='center' >
                <MDBIcon fas icon="user-circle" className='fa-2x' />
                <h5>Sign in</h5>
                <MDBCardBody>
                    <MDBValidation onSubmit={onSubmit} noValidate className='row g-3'>
                        <MDBValidationItem
                            feedback="Please provide your email"
                            invalid
                            className="col-md-12" >
                            <MDBInput
                                label="Email"
                                type="email"
                                name="email"
                                value={formValue.email}
                                onChange={onFormChange}
                                required
                            />
                        </MDBValidationItem>
                        <MDBValidationItem
                            feedback="Please provide your password"
                            invalid
                            className="col-md-12" >
                            <MDBInput
                                label="Passowrd"
                                type="password"
                                name="password"
                                value={formValue.password}
                                onChange={onFormChange}
                                required
                            />
                        </MDBValidationItem>
                        <div className='col-md-12'>
                            <MDBBtn
                                className='mt-2 col-md-12'
                                size='sm'
                                role="status"
                            >
                                Login
                            </MDBBtn>
                        </div>
                    </MDBValidation>
                </MDBCardBody>
                <MDBFooter>
                    <Link to="/signup">
                        <p>Don't have an account? Sign Up</p>
                    </Link>
                </MDBFooter>
            </MDBCard>
        </div>
    );
}
