import { ActionTypes } from "../action-types";
import { SigninActions } from '../actions/signin-action';
import { SignupActions } from "../actions/signup-action";
import {
    signin as signinRoute,
    signup as signupRoute,
    createTour as createTourRoute
} from '../../api'
import { Dispatch } from "react";
import localforage from "localforage";
import { NavigateFunction } from "react-router-dom";
import { toast as toastType } from "react-toastify";
import { AxiosError } from "axios";
import { Tour } from "../../../types";
import { TourActions } from "../actions/tour-action";


interface AuthParams {
    email: string;
    password: string;
    navigate: NavigateFunction;
    toast: typeof toastType;
}

interface SignupParams extends AuthParams {
    name: string
};



export const signin = (signinParams: AuthParams) => {
    return async (dispatch: Dispatch<SigninActions>) => {
        dispatch({
            type: ActionTypes.SIGNIN_LOADING,
        });
        try {
            const response = await signinRoute(signinParams)
            console.log(response);
            await localforage.setItem('profile', JSON.stringify(response.data));
            signinParams.toast.success("Login Succesfully!");
            signinParams.navigate('/');
            dispatch({
                type: ActionTypes.SIGN_IN,
                payload: response.data
            });
        } catch (error) {
            dispatch({
                type: ActionTypes.SIGNIN_ERROR,
                payload: { error: formatError(error) }
            });
        }
    }
}



export const signup = (signupParams: SignupParams) => {
    return async (dispatch: Dispatch<SignupActions>) => {
        dispatch({
            type: ActionTypes.SIGNUP_LOADING
        });
        try {
            const { data } = await signupRoute(signupParams);
            dispatch({
                type: ActionTypes.SIGN_UP,
                payload: data
            });
            signupParams.toast("Singup successfully");
            signupParams.navigate('/');
        } catch (error) {
            dispatch({
                type: ActionTypes.SIGNUP_ERROR,
                payload: { error: formatError(error) }
            });
        }
    }
}


export const setUser = () => {
    return async (dispatch: Dispatch<SigninActions>) => {
        const user = (await localforage.getItem("profile")) as string;
        if (user) {
            dispatch({
                type: ActionTypes.SET_USER,
                payload: {
                    user: JSON.parse(user).user
                }
            });
        }
    }
}


export const signout = () => {
    return async (dispatch: Dispatch<SigninActions>) => {
        await localforage.clear()
        dispatch({ type: ActionTypes.SIGN_OUT });
    }
}


export const createTour = (tourData: Tour) => {
    return async (dispatch: Dispatch<TourActions>) => {
        dispatch({ type: ActionTypes.TOUR_CREATE });
        try {
            const { data } = await createTourRoute(tourData);
            // navigate to home
            // toast the result
            dispatch({ type: ActionTypes.TOUR_CREATE_FINISHED });
        } catch (error) {
            dispatch({
                type: ActionTypes.TOUR_CREATE_ERROR, payload: {
                    error: formatError(error)
                }
            });
        }
    }
}


const formatError = (error: any): string => {
    return error instanceof AxiosError
        ? error.response?.data.error
        : "Something went wrong on the server";
}
