import { AuthState, INITIAL_STATE } from "."
import { ActionTypes } from "../action-types";
import { SignupActions } from "../actions/signup-action"






const signupReducer = (state: AuthState = INITIAL_STATE, action: SignupActions): AuthState => {
    switch (action.type) {
        case ActionTypes.SIGN_UP:
            return { ...state, user: action.payload.user, loading: false, error: "" };
        case ActionTypes.SIGNUP_ERROR:
            return { ...state, error: action.payload.error };
        case ActionTypes.SIGNUP_LOADING:
            return { ...state, loading: true };
        default:
            return INITIAL_STATE;
    }
};


export default signupReducer;
