import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { toast as ToastType } from 'react-toastify';
import { AxiosError } from "axios";
import { NavigateFunction } from "react-router-dom";
import { signin as signinRoute } from '../../api';
import { InitialState } from "@react-navigation/native";
import localforage from "localforage";


interface AuthState {
    user: {
        name: string;
        email: string;
        id: string;
    } | null;
    loading: boolean;
    error: string;
}

const initialState: AuthState = {
    user: null,
    error: "",
    loading: false
};


interface AuthParams {
    email: string;
    password: string;
    navigate: NavigateFunction;
    toast: typeof ToastType;
}

const formatError = (error: any): string => {
    return error instanceof AxiosError
        ? error.response?.data.error
        : "Something went wrong on the server";
}

export const login = createAsyncThunk(
    "/auth/signin",
    async (signinParams: AuthParams, { rejectWithValue }) => {
        try {
            const response = await signinRoute<AuthState>(signinParams)
            signinParams.toast.success("Login Succesfully!");
            signinParams.navigate('/');
            return response.data;
        } catch (error) {
            if (error instanceof Error) {
                return rejectWithValue(formatError(error));
            }
        }
    })

const authSlice = createSlice({
    name: "Auth",
    initialState,
    reducers: {
    },
    extraReducers: (builder) => {
        builder.addCase(login.pending, (state: AuthState, action) => {
            state.loading = true;
        });

        builder.addCase(login.fulfilled, (state: AuthState, { payload }) => {
            if (!payload) {
                return;
            }
            (async () => await localforage.setItem("profile", JSON.stringify(payload.user)))();
            state.user = payload.user;
        });


        builder.addCase(login.rejected, (state: AuthState, action: {type: ""}) => {
        });
    }
});


