import { ActionTypes } from "../action-types";
import { TourActions } from "../actions/tour-action";
import { TourData } from './';


const INITIAL_STATE: TourData = {
    loading: false,
    error: ""
}

const tourReducer = (state: TourData = INITIAL_STATE, action: TourActions): TourData => {
    switch (action.type) {
        case ActionTypes.TOUR_CREATE:
            return { loading: true, error: "" }
        case ActionTypes.TOUR_CREATE_FINISHED:
            return { loading: false, error: "" };
            case ActionTypes.
        default:
            return INITIAL_STATE;
    }
}

export default tourReducer;
