import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import toastType from 'react-toastify';
import { AxiosError } from "axios";
import { NavigateFunction } from "react-router-dom";
import { ActionTypes } from "../action-types";
import { SigninActions } from "../actions/signin-action";
import { AuthState, INITIAL_STATE } from './';
import { signin as signinRoute } from '../../api/';


const initialState: AuthState = {
    user: null,
    error: "",
    loading: false
};


interface AuthParams {
    email: string;
    password: string;
    navigate: NavigateFunction;
    toast: typeof toastType;
}

const formatError = (error: any): string => {
    return error instanceof AxiosError
        ? error.response?.data.error
        : "Something went wrong on the server";
}

const login = createAsyncThunk(
    "/auth/signin",
    async (signinParams: AuthParams, { rejectWithValue }) => {
        try {
            const response = signinRoute(signinParams)
            signinParams.toast.success("Login Succesfully!");
            signinParams.navigate('/');
        } catch (error) {
            if (error instanceof Error) {
                return rejectWithValue(formatError(error));
            }
        }
    })

const authSlice = createSlice({
    name: "Auth",
    initialState,
    reducers: {},
    extraReducers: {}
});


const signinReducer = (state: AuthState = INITIAL_STATE, action: SigninActions): AuthState => {
    switch (action.type) {
        case ActionTypes.SIGN_IN:
            return { ...state, user: action.payload.user, loading: false, error: "" };
        case ActionTypes.SIGNIN_ERROR:
            return { ...state, error: action.payload.error };
        case ActionTypes.SET_USER:
            return { ...state, user: action.payload.user };
        case ActionTypes.SIGNIN_LOADING:
            return { ...state, loading: true };
        case ActionTypes.SIGN_OUT:
            return INITIAL_STATE;
        default:
            return INITIAL_STATE;
    }
}


export default authSlice.reducer;
