import React, { useEffect } from 'react'
import { useAppDispatch, useAppSelector } from '../hooks'
import { MDBSpinner } from 'mdb-react-ui-kit';
import { getToursByUser } from '../states/reducers/tour-reducer';
import { toUSVString } from 'util';


const Dashboard = () => {
    const { auth, tour } = useAppSelector(tourData => tourData);
    const dispatch = useAppDispatch();

    useEffect(() => {
        if (!auth.user?.id) {
            return;
        }
        dispatch(getToursByUser(auth.user.id));
    }, []);

    if (tour.loading) {
        return <MDBSpinner size="lg" />
    }
    return (
        <div
            style={{
                alignContent: "center",
                margin: "auto"
            }}
        >
            <h2 className='text-center'>Dashboard: {auth.user?.name}</h2>
            <hr style={{ maxWidth: "500px" }} />
            {
                tour.userTours && tour.userTours.map(tour => {
                    return <h1>{tour.name}</h1>
                })
            }
        </div>
    );
}

export default Dashboard;
