import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { ActionTypes } from "../action-types";
import { SigninActions } from "../actions/signin-action";
import { AuthState, INITIAL_STATE } from './';


const initialState: AuthState = {
    user: null,
    error: "",
    loading: false
};


export const signin = (signinParams: AuthParams) => {
    return async (dispatch: Dispatch<SigninActions>) => {
        dispatch({
            type: ActionTypes.SIGNIN_LOADING,
        });
        try {
            const response = await signinRoute(signinParams)
            await localforage.setItem('profile', JSON.stringify(response.data));
            signinParams.toast.success("Login Succesfully!");
            signinParams.navigate('/');
            dispatch({
                type: ActionTypes.SIGN_IN,
                payload: response.data
            });
        } catch (error) {
            dispatch({
                type: ActionTypes.SIGNIN_ERROR,
                payload: { error: formatError(error) }
            });
        }
    }
}

const login = createAsyncThunk(
    "/auth/signin",
    async (signinParams: AuthParams, { rejectWithValue }) => {
        try {
            const response = await signinRoute(signinParams)
            signinParams.toast.success("Login Succesfully!");
            signinParams.navigate('/');
        } catch (error) {
            return reje 
        }
    })

const authSlice = createSlice({
    name: "Auth",
    initialState,
    reducers: {},
    extraReducers: {}
});


const signinReducer = (state: AuthState = INITIAL_STATE, action: SigninActions): AuthState => {
    switch (action.type) {
        case ActionTypes.SIGN_IN:
            return { ...state, user: action.payload.user, loading: false, error: "" };
        case ActionTypes.SIGNIN_ERROR:
            return { ...state, error: action.payload.error };
        case ActionTypes.SET_USER:
            return { ...state, user: action.payload.user };
        case ActionTypes.SIGNIN_LOADING:
            return { ...state, loading: true };
        case ActionTypes.SIGN_OUT:
            return INITIAL_STATE;
        default:
            return INITIAL_STATE;
    }
}


export default signinReducer;
